import React from 'react'
import { FormGroup, Label, Input, Collapse } from 'reactstrap'
import './sidefiltre.css'

const sidefilter = props => {
    //const [isOpen, setIsOpen] = useState(false);
    React.State ={
        isOpen: false,
        setIsOpen: false
      }
      
    const toggle = () => React.state.setIsOpen(!React.state.isOpen);
    const Openstate = () => this.isOpen(React.state.isOpen); 
    return(
    <div className="firstfilter">
        <Collapse isOpen={Openstate}>
            <div>
                <p onClick={toggle} > Annonces créées depuis </p>
                <FormGroup check>
                    <Label check>
                        <Input type="radio" name="radio1" />{' '}
                        Un jour
                    </Label>
                </FormGroup>
            </div>
            <div>
                <FormGroup check>
                    <Label check>
                        <Input type="radio" name="radio1" />{' '}
                        Deux jour
                    </Label>
                </FormGroup>
            </div>
            <div>
                <FormGroup check>
                    <Label check>
                        <Input type="radio" name="radio1" />{' '}
                        Un jour
                    </Label>
                </FormGroup>
            </div>
            <div>
                <FormGroup check>
                    <Label check>
                        <Input type="radio" name="radio1" />{' '}
                        Un jour
                    </Label>
                </FormGroup>
            </div>
            <div>
                <FormGroup check>
                    <Label check>
                        <Input type="radio" name="radio1" />{' '}
                        Un Mois
                    </Label>
                </FormGroup>
            </div> 
            </Collapse>   
        </div>
    );
    
};

export default sidefilter;
