import React, { useEffect,useState } from 'react'
import {Card, CardBody, CardHeader, CardText} from 'reactstrap'

const listCategorie = () =>  {
     
    const getAnnonce = async () => {
        try{
            const response = await fetch("http://localhost:3001/annonce");
            const annonceData = await response.json();
            // this.setState(annonceData); //set state to assign data fetch from backend
            console.log(annonceData);
        }catch(err){
            console.error(err.message);
        }
    };

    useEffect(() => {
        getAnnonce();
    },[]);

        return(
            <div className="listAnnonce" >
                {this.state.Annonces.map(annonces =>{
                    return(
                        <div>
                            <Card>
                                <CardBody>
                                    <CardHeader>{annonces.title}</CardHeader>
                                    <CardText>{annonces.description}</CardText>
                                </CardBody>
                            </Card> <br/>
                        </div>
                    )
                })}
            </div>
        )
}

export default listCategorie;
