import React, { Component } from 'react'
import { FormGroup, Label, Input, Collapse } from 'reactstrap'
import './sidefiltre.css'

class sidefilter extends Component {  
    constructor(props){
        super(props);
        this.state = {isopen:false}
    }
    State ={
        isopen:false
    }
        
    openFilter = () => {
        this.setState((prevState) => {
            return {isopen: !prevState.isopen};
          })
    }
    render(){
        return(
            <div className="firstfilter">
                <p onClick={this.openFilter} > Annonces créées depuis </p>
                <Collapse isOpen={this.isopen}>
                    <div>
                        <FormGroup check>
                            <Label check>
                                <Input type="radio" name="radio1" />{' '}
                                Un jour
                            </Label>
                        </FormGroup>
                    </div>
                    <div>
                        <FormGroup check>
                            <Label check>
                                <Input type="radio" name="radio1" />{' '}
                                Deux jour
                            </Label>
                        </FormGroup>
                    </div>
                    <div>
                        <FormGroup check>
                            <Label check>
                                <Input type="radio" name="radio1" />{' '}
                                Un jour
                            </Label>
                        </FormGroup>
                    </div>
                    <div>
                        <FormGroup check>
                            <Label check>
                                <Input type="radio" name="radio1" />{' '}
                                Un jour
                            </Label>
                        </FormGroup>
                    </div>
                    <div>
                        <FormGroup check>
                            <Label check>
                                <Input type="radio" name="radio1" />{' '}
                                Un Mois
                            </Label>
                        </FormGroup>
                    </div> 
                    </Collapse>   
                </div>
        );
        
        }
    
};

export default sidefilter;
