import React, { Component } from 'react';
import './App.css';
import Menu from './components/Menubar/Menu';
import SideBarDraw from './components/Sidebar/SideBarDraw';
import Backdrop from './components/Backdrop/Backdrop';
import Categorie from './components/Categorie/categorie'
import Fistfilter from './components/Sidefilter/firstFiltre'
import Secondfilter from './components/Sidefilter/secondFilter'
import Thirdfilter from './components/Sidefilter/thirdFilter'
import Recherche from './components/'

class App extends Component {
  state ={
    sideBarDrawOpen: false
  }
  drawerToggleClickHandler = () => {
    this.setState((prevState) => {
      return {sideBarDrawOpen: !prevState.sideBarDrawOpen};
    });
  };

  backdropClickHandler = () => {
    this.setState({sideBarDrawOpen: false})
   }
  render(){
    let sideDrawer;
    let backdrop;

    if(this.state.sideBarDrawOpen){
      sideDrawer = <SideBarDraw />
      backdrop = <Backdrop click={this.backdropClickHandler}/>
    }
    return (
      <div style={{height: '100%'}} className="parent">
          <Menu drawerClickHandler={this.drawerToggleClickHandler}/>
          {sideDrawer}
          {backdrop}

          <div className="filtre">
              <Fistfilter/>
              <Secondfilter/>
              <Thirdfilter/>
          </div>
          <div className="main">
              <div className="categorie">
                <Categorie />        
              </div>
          </div>
          <div className="newannonce">

          </div>
      </div>
    );
  }
}
export default App;
