import React, { Component } from 'react';
import './App.css';
import Menu from './components/Menubar/Menu';
import SideBarDraw from './components/Sidebar/SideBarDraw';
import Backdrop from './components/Backdrop/Backdrop';

class App extends Component {
  state ={
    sideBarDrawOpen: false
  }
  drawerToggleClickHandler = () => {
    this.setState((prevState) => {
      return {sideBarDrawOpen: !prevState.sideBarDrawOpen};
    });
  };

  backdropClickHandler = () => {
    this.setState({sideBarDrawOpen: false})
   }
  render(){
    let sideDrawer;
    let backdrop;

    if(this.state.sideBarDrawOpen){
      sideDrawer = <SideBarDraw />
      backdrop = <Backdrop click={this.backdropClickHandler}/>
    }
    return (
      <div style={{height: '100%'}}>
          <Menu drawerClickHandler={this.drawerToggleClickHandler}/>
          {sideDrawer}
          {backdrop}
          <div className="Filtre">

          </div>
          <main style={{marginTop: '64px'}}>
              <p> This is the content page </p>
          </main>
          <div>

          </div>
      </div>

    );
  }

}
export default App;
